import { useContext, useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { formatISO9075 } from "date-fns";
import { UserContext } from "./UserContext";

export default function PostPage() {
  const [postInfo, setPostInfo] = useState(null);
  const { userInfo } = useContext(UserContext);
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:4000/post/${id}`)
      .then(response => response.json())
      .then(postInfo => setPostInfo(postInfo));
  }, [id]);

  if (!postInfo) return '';

  const postPageStyle = {
    padding: '20px',
    margin: '20px auto',
    width: '80%',
    maxWidth: '800px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    fontFamily: 'Arial, sans-serif',
  };

  const titleStyle = {
    fontSize: '2rem',
    fontWeight: 'bold',
    marginBottom: '10px',
    color: '#333',
  };

  const timeStyle = {
    color: '#888',
    marginBottom: '10px',
  };

  const authorStyle = {
    color: '#555',
    fontStyle: 'italic',
    marginBottom: '20px',
  };

  const editBtnStyle = {
    display: 'inline-block',
    padding: '10px 15px',
    backgroundColor: '#007BFF',
    color: '#fff',
    borderRadius: '5px',
    textDecoration: 'none',
    cursor: 'pointer',
    marginBottom: '20px',
    display: userInfo?.id === postInfo.author._id ? 'block' : 'none', // only show for the author
  };

  const imageStyle = {
    width: '100%',
    borderRadius: '8px',
    marginBottom: '20px',
  };

  const contentStyle = {
    color: '#444',
    lineHeight: '1.6',
  };

  return (
    <div style={postPageStyle}>
      <h1 style={titleStyle}>{postInfo.title}</h1>
      <time style={timeStyle}>{formatISO9075(new Date(postInfo.createdAt))}</time>
      <div style={authorStyle}>by @{postInfo.author.username}</div>
      {userInfo?.id === postInfo.author._id && (
        <div>
          <Link style={editBtnStyle} to={`/edit/${postInfo._id}`}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              style={{ width: '20px', height: '20px', marginRight: '5px', verticalAlign: 'middle' }}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M16.862 4.487l1.687-1.688a1.875 1.875 0 112.652 2.652L10.582 16.07a4.5 4.5 0 01-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 011.13-1.897l8.932-8.931zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0115.75 21H5.25A2.25 2.25 0 013 18.75V8.25A2.25 2.25 0 015.25 6H10"
              />
            </svg>
            Edit this post
          </Link>
        </div>
      )}
      <div>
        <img style={imageStyle} src={`http://localhost:4000/uploads/${postInfo.cover}`} alt={postInfo.title} />
      </div>
      <div style={contentStyle} dangerouslySetInnerHTML={{ __html: postInfo.content }} />
    </div>
  );
}
