import React, { useState } from 'react';
import { Navigate } from 'react-router-dom'; // For navigation on successful login

const Loginpage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [redirect, setRedirect] = useState(false);

  async function login(ev) {
    ev.preventDefault();
    const response = await fetch('http://localhost:4000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
      credentials: 'include', // Ensure cookies are stored on login
    });

    if (response.ok) {
      const data = await response.json();
      localStorage.setItem('token', data.token); // Optionally store token in localStorage
      setRedirect(true); // Set redirect to true to navigate to home
      console.log('Login successful:', data);
    } else {
      console.error('Login failed:', response.statusText);
    }
  }

  if (redirect) {
    return <Navigate to="/" />; // Navigate to home page after successful login
  }

  return (
    <form onSubmit={login} style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Login</h1>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={ev => setUsername(ev.target.value)}
        style={{ marginBottom: '10px', padding: '8px', width: '250px' }}
      />
      <br />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={ev => setPassword(ev.target.value)}
        style={{ marginBottom: '10px', padding: '8px', width: '250px' }}
      />
      <br />
      <button
        type="submit"
        style={{
          padding: '10px 20px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        Login
      </button>
    </form>
  );
};

export default Loginpage;
