import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Header = () => {
  const [username, setUsername] = useState(null);
  const navigate = useNavigate(); // Used to redirect

  useEffect(() => {
    // Check for token in localStorage and fetch user profile
    const token = localStorage.getItem('token');
    if (token) {
      fetch('http://localhost:4000/profile', {
        credentials: 'include', // Include cookies
      }).then(response => {
        if (response.ok) {
          response.json().then(userInfo => {
            setUsername(userInfo.username);
          });
        }
      });
    }
  }, []); // Empty dependency array ensures this runs only once on mount

  function logout() {
    fetch('http://localhost:4000/logout', {
      credentials: 'include',
      method: 'POST',
    }).then(() => {
      localStorage.removeItem('token'); // Clear token from localStorage
      setUsername(null);
      navigate('/login'); // Redirect to login page
    });
  }

  return (
    <header>
      <Link to="/" className="logo">MYblog</Link>
      <nav>
        {username ? (
          <>
            <Link to="/create">Create new post</Link>
            <button onClick={logout} style={{ background: 'none', border: 'none', color: 'blue', textDecoration: 'underline', cursor: 'pointer' }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
