import ReactQuill from "react-quill";
import 'react-quill/dist/quill.snow.css'; // Import the CSS for the snow theme
import React, { forwardRef } from "react";


export default function Editor({value,onChange}) {

  useEffect(() => {
    const editorContainer = document.querySelector('.ql-editor'); // Grabbing the Quill editor

    if (editorContainer) {
      const config = { childList: true, subtree: true };

      const callback = (mutationsList) => {
        for (let mutation of mutationsList) {
          if (mutation.type === 'childList') {
            console.log('Quill content changed');
          }
        }
      };

      const observer = new MutationObserver(callback);
      observer.observe(editorContainer, config);

      // Clean-up the observer when the component unmounts
      return () => observer.disconnect();
    }
  }, []);
  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
      [
        { list: 'ordered' },
        { list: 'bullet' },
        { indent: '-1' },
        { indent: '+1' },
      ],
      ['link', 'image'],
      ['clean'],
    ],
  };
  return (
    <div className="content">
    <ReactQuill
      value={value}
      theme={'snow'}
      onChange={onChange}
      modules={modules} />
    </div>
  );
}
