import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // useNavigate for redirection

const Header = () => {
  const [username, setUsername] = useState(null);
  const navigate = useNavigate(); // Initialize useNavigate for redirection

  useEffect(() => {
    fetch('http://localhost:4000/profile', {
      credentials: 'include', // Include cookies to fetch the token from cookies
    }).then(response => {
      response.json().then(userInfo => {
        if (userInfo.username) {
          setUsername(userInfo.username);
        }
      });
    });
  }, []);

  function logout() {
    fetch('http://localhost:4000/logout', {
      credentials: 'include', // Include credentials for logging out and clearing cookies
      method: 'POST',
    }).then(() => {
      setUsername(null); // Clear username state
      localStorage.removeItem('token'); // Optionally remove token from localStorage
      navigate('/login'); // Redirect to login page
    });
  }

  return (
    <header>
      <Link to="/" className="logo">MYblog</Link>
      <nav>
        {username && (
          <>
            <Link to="/create">Create new post</Link>
            <button
              onClick={logout}
              style={{ background: 'none', border: 'none', color: 'blue', textDecoration: 'underline', cursor: 'pointer' }}
            >
              Logout ({username})
            </button>
          </>
        )}
        {!username && (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
