import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const modules = {
  toolbar: [
    [{ 'header': '1' }, { 'header': '2' }, { 'font': [] }],
    ['bold', 'italic', 'underline', 'strike', 'blockquote'],
    [{ 'list': 'ordered' }, { 'list': 'bullet' }, { 'indent': '-1' }, { 'indent': '+1' }],
    ['link', 'image'],
    [{ 'align': [] }],
    ['clean'], // Remove formatting button
  ],
};

const formats = [
  'header', 'font', 'bold', 'italic', 'underline', 'strike', 'blockquote',
  'list', 'bullet', 'indent', 'link', 'image', 'align',
];

const CreatePost = () => {
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
  const [file, setFiles] = useState(null); // Changed to null

  function createNewPost(ev) {
    ev.preventDefault(); // Move to the start of the function
    const data = new FormData();
    data.set('title', title);
    data.set('summary', summary);
    data.set('content', content);

    if (file) { // Only set the file if it exists
      data.set('file', file[0]); // Set the first file
    }

    // console.log(file); // Changed from files to file

    // Uncomment and modify this section for your API call
    // fetch('http://localhost:4000/post', {
    //     method: 'POST',
    //     body: data, // Make sure to send FormData
    // })
  }

  return (
    <form onSubmit={createNewPost}>
      <input
        type="text" // Changed from title
        placeholder="Title"
        value={title}
        onChange={(ev) => setTitle(ev.target.value)}
      />
      <input
        type="text" // Changed from summary
        placeholder="Summary"
        value={summary}
        onChange={(ev) => setSummary(ev.target.value)}
      />
      <input
        type="file"
        onChange={(ev) => setFiles(ev.target.files)} // Changed to files
      />
      <ReactQuill
        value={content}
        onChange={setContent}
        modules={modules}
        formats={formats}
      />
      <button style={{ marginTop: '5px' }}>Create Post</button>
    </form>
  );
}

export default CreatePost;
