import { formatISO9075 } from "date-fns";
import { Link } from "react-router-dom";

export default function Post({ _id, title, summary, cover, content, createdAt, author }) {
  return (
    <div style={postStyle}>
      <div style={imageContainerStyle}>
        <Link to={`/post/${_id}`}>
          <img
            src={`http://localhost:4000/uploads/${cover}`}
            alt={title}
            style={imageStyle}
          />
        </Link>
      </div>
      <div style={textsStyle}>
        <div style={editButtonContainerStyle}>
          <Link to={`/edit/${_id}`} style={editButtonStyle}>Edit</Link>
        </div>
        <Link to={`/post/${_id}`} style={titleLinkStyle}>
          <h2 style={titleStyle}>{title}</h2>
        </Link>
        <p style={infoStyle}>
          <span style={authorStyle}>{author?.username || "Unknown Author"}</span>
          <time style={timeStyle}>{formatISO9075(new Date(createdAt))}</time>
        </p>
        <p style={summaryStyle}>{summary}</p>
      </div>
    </div>
  );
}

// Inline styles
const postStyle = {
  border: '1px solid #ddd',
  borderRadius: '8px',
  overflow: 'hidden',
  boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  backgroundColor: '#fff',
  transition: 'transform 0.2s',
  padding: '15px',
  margin: '20px 0',
};

const imageContainerStyle = {
  width: '100%',
  height: '200px',
  overflow: 'hidden',
  borderRadius: '8px',
};

const imageStyle = {
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  transition: 'transform 0.2s',
};

const textsStyle = {
  padding: '15px',
  position: 'relative',
};

const editButtonContainerStyle = {
  textAlign: 'center',
  marginBottom: '10px',
};

const editButtonStyle = {
  display: 'inline-block',
  backgroundColor: '#007BFF',
  color: '#fff',
  padding: '8px 12px',
  borderRadius: '4px',
  textDecoration: 'none',
  fontWeight: 'bold',
  transition: 'background-color 0.2s',
};

const titleLinkStyle = {
  textDecoration: 'none',
  color: '#333',
};

const titleStyle = {
  fontSize: '24px',
  margin: '10px 0',
};

const infoStyle = {
  color: '#666',
  fontSize: '14px',
  margin: '10px 0',
};

const authorStyle = {
  fontWeight: 'bold',
  marginRight: '10px',
};

const timeStyle = {
  color: '#999',
};

const summaryStyle = {
  color: '#444',
  marginTop: '10px',
};

// Adding hover effects for image zoom
imageContainerStyle[':hover'] = {
  transform: 'scale(1.05)',
  transition: 'transform 0.2s',
};
